import Preset from './Preset';
import ResponseConfigurator from './helpers/ResponseConfigurator';
import RequestMatcher from './helpers/RequestMatcher';
import FMFException from './helpers/FMFException';
import pathToRegexp from 'path-to-regexp';

/**
 * A fixture contains all the needed informations to find
 * out wich request can be matched and wich response
 * must be sent back
 * @version 1.0.0
 * @since 2.0.0
 */
export class Fixture extends ResponseConfigurator {
  /**
   * Stores the number of responses generated by this fixture
   * @type {Number}
   * @since 2.0.0
   */
  calls = 0;

  /**
   * Stores the response which are specific to own call count
   * @type {Array}
   * @since 2.0.0
   */
  ownCalls = [];

  /**
   * Stores the response which are specific to global call count
   * @type {Array}
   * @since 2.0.0
   */
  globalCalls = [];

  /**
   * Flag for response configuration
   * @type {null|Number}
   * @since 2.0.0
   */
  _callnum = null;

  /**
   * Request matcher instance
   * @type {null|RequestMatcher}
   * @since 2.0.0
   */
  _matcher = null;

  /**
   * Flag for call count configuration
   * @type {Boolean}
   * @since 2.0.0
   */
  _ownCall = false;

  /**
   * Allow response key for configuration
   * @type {Array}
   * @since 2.0.0
   */
  _responseKeys = ['body', 'delay', 'headers', 'status', 'statusText', 'wrapper', 'pattern', 'before', 'after', 'preset'];

  /**
   * Initialize the ResponseConfigurator helper
   * @version 1.0.0
   * @since   2.0.0
   * @param   {Server}  [server] Server instance
   */
  constructor(server) {
    super(server);
  }

  /**
   * Set the fixture in request matching mode. The matcher is
   * behing a proxy to allow quick property mapping
   *
   * @version 1.0.0
   * @since   2.0.0
   * @return  {RequestMatcher}  Request matcher
   */
  get on() {
    if (this._mode === 'respond') return this.server.on;

    this._matcher = this._matcher || new RequestMatcher(this);

    return new Proxy(this._matcher, {
      get: (obj, prop) => prop in obj ? obj[prop] : obj.getProcessor(prop)
    });
  }

  /**
   * Alias for `on`
   * @version 1.0.0
   * @since   2.0.0
   * @return  {RequestMatcher}  Request matcher
   */
  get when() {
    return this.on;
  }

  /**
   * Ask server to process the respond. If the fixture is already in response
   * mode, a new fixture will be returned. Otherwise, it will be the same fixture
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Fixture}  Fixture
   */
  get respond() {
    return this.server ? this.server._processRespond(this) : this;
  }

  /**
   * Target the current response set based on call count configuration
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Object}  Response configuration object
   */
  _getCurrentResponseSet() {
    if (this._callnum !== null) {
      if (this._ownCall) {
        this.ownCalls[this._callnum] = this.ownCalls[this._callnum] || {}
        return this.ownCalls[this._callnum];
      }
      this.globalCalls[this._callnum] = this.globalCalls[this._callnum] || {}
      return this.globalCalls[this._callnum];
    }

    this._any = this._any;
    return this._any;
  }

  /**
   * Syntax sugar for human readability
   * @version 1.0.0
   * @since   1.0.0
   * @return  {Fixture}
   */
  get to() {
    return this;
  }

  /**
   * Alias for respond that always return the fallback fixture
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Fixture}  Fallback fixture
   */
  get fallback() {
    return this.server.fallback;
  }

  firstCall(own = false) {
    this._callnum = 1;
    this._ownCall = own;

    return this;
  }

  secondCall(own = false) {
    this._callnum = 2;
    this._ownCall = own;

    return this;
  }

  thirdCall(own = false) {
    this._callnum = 3;
    this._ownCall = own;

    return this;
  }

  call(n, own) {
    this._callnum = parseInt(n, 10);
    this._ownCall = own;

    return this;
  }

  get any() {
    this._callnum = null;
    this._ownCall = false;

    return this;
  }

  preset(name) {
    let response = this._getCurrentResponseSet();

    if (name) response.preset = name;
    else delete response.preset;

    return this;
  }

  async match(request, server) {
    /* istanbul ignore if */
    if (!this._matcher) return true;

    return await this._matcher.match(request, server);
  }

  async sleep(delay) {
    await new Promise(resolve => setTimeout(resolve, delay));
  }

  extractParams(pathname, pattern) {
    const params = {};
    const keys = [];
    const re = pathToRegexp(pattern, keys);
    const parsed = re.exec(pathname);

    for (let i = 0; i < keys.length; i++) {
      let name = keys[i].name;
      let value = parsed[i+1];

      params[name] = value;
    }

    return params;
  }

  wrap(body, wrapper) {
    if (wrapper instanceof Function) return wrapper(body);

    return body || null;
  }

  getPath(request, response) {
    let url = response.pattern || request.pathname;
    let path = url.split('/').filter(p => p && p.indexOf(':') < 0);
    let filename = request.method.toLowerCase();

    return path.concat(filename).join('/');
  }

  _exceptionManagement(err, message) {
    if (err instanceof Error || typeof err === 'string') {
      throw new FMFException(message, err);
    }

    if (err instanceof Response) {
      return err;
    }

    if (err instanceof Preset) {
      let {body, headers, status, statusText} = err._any;

      return new Response(body, {headers, status, statusText});
    }

    /* istanbul ignore else */
    if (err instanceof Object) {
      let {body, headers, status, statusText} = err;

      return new Response(body, {headers, status, statusText});
    }
  }

  async _buildResponse(request, response) {
    // Process before hook and update response if one is returned
    if (response.before instanceof Function) {
      try {
        let responseReplacement = await response.before.call(this, this.server, request, response);

        if (responseReplacement) response = responseReplacement;
      } catch (err) {
        return this._exceptionManagement(err, 'Unable to process before callback');
      }
    }

    // Extract params if a pattern have been set
    let params = response.pattern ? this.extractParams(request.pathname, response.pattern) : {};

    // Process body callback
    try {
      if (response.body instanceof Function) response.body = await response.body.call(this, params, {
        request,
        response,
        server: this.server
      });
    } catch (err) {
      return this._exceptionManagement(err, 'Unable to process body callback');
    }

    // Apply preset
    if (response.preset) {
      let preset = this.server._presets[response.preset];

      /* istanbul ignore if */
      if (!preset) throw new FMFException(`Unable to apply preset. Preset ${response.preset} is not defined`);
      response = Object.assign(response, preset._any);
    }

    // Construct response
    let {body, headers, status, statusText, wrapper} = response;

    const responseObject = new Response(this.wrap(body, wrapper), {headers, status, statusText});

    // Process after hook
    try {
      if (response.after instanceof Function) await response.after.call(this, this.server, responseObject);
    } catch (err) {
      return this._exceptionManagement(err, 'Unable to process after callback');
    }

    // Delay response
    if (response.delay) await this.sleep(response.delay);

    return responseObject;
  }

  getResponse(request) {
    const localCalls = ++this.calls;
    const globalCalls = this.server.calls;

    // Match local calls first
    return this._buildResponse(request,
      this.ownCalls[localCalls] ||
      this.globalCalls[globalCalls] ||
      this._any
    );
  }
}

export default Fixture;

export default class FMFException extends Error {
  constructor(message, previous = null) {
    super(message);

    this.previous = previous;

    /* istanbul ignore else */
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, FMFException);
    }
  }

  toString() {
    return `FMF error: ${this.message}`;
  }
}

import parse from 'url-parse';

export class FMFRequest extends Request {
  constructor(resource, init) {
    super(resource, init);

    const parts = parse(this.url, true);

    for (let key in parts) {
      Object.defineProperty(this, key, {
        enumerable: true,
        value: parts[key]
      })
    }
  }

  clone() {
    return new FMFRequest(super.clone());
  }
}

export default FMFRequest;

import BooleanProcessor from '@/processors/BooleanProcessor';
import BodyProcessor from '@/processors/BodyProcessor';
import HeadersProcessor from '@/processors/HeadersProcessor';
import QueryProcessor from '@/processors/QueryProcessor';
import StringProcessor from '@/processors/StringProcessor';
import FMFException from '@/helpers/FMFException';

export class RequestMatcher {
  _processors = [];
  _requestKeys = [
    'method', 'url', 'headers', 'destination', 'referrer', 'referrerPolicy', 'mode',
    'credentials', 'redirect', 'integrity', 'cache',
    'protocol', 'slashes', 'auth', 'username', 'password', 'host', 'hostname', 'port',
    'pathname', 'query', 'hash', 'href', 'method', 'header', 'body'
  ];

  constructor(fixture) {
    this.fixture = fixture;
  }

  get on() {
    return this.fixture.on;
  }

  get and() {
    return this;
  }

  get respond() {
    return this.fixture.respond;
  }

  getProcessor(key) {
    let processor;

    switch (key) {
      case 'headers':
        processor = new HeadersProcessor(key, this);
        break;
      case 'query':
        processor = new QueryProcessor(key, this);
        break;
      case 'slashes':
        processor = new BooleanProcessor(key, this);
        break;
      case 'auth':
      case 'cache':
      case 'credentials':
      case 'destination':
      case 'hash':
      case 'href':
      case 'host':
      case 'hostname':
      case 'integrity':
      case 'mode':
      case 'method':
      case 'password':
      case 'pathname':
      case 'port':
      case 'protocol':
      case 'redirect':
      case 'referrer':
      case 'referrerPolicy':
      case 'url':
      case 'username':
        processor = new StringProcessor(key, this)
        break;
      default:
        throw new FMFException(`Unsupported request parameter "${key}" to check`);
    }

    this._processors.push(processor);

    return processor;
  }

  body(type = 'text') {
    const processor = new BodyProcessor('headers', this, type);

    this._processors.push(processor);

    return processor;
  }

  header(name) {
    const processor = new HeadersProcessor('headers', this, name);

    this._processors.push(processor);

    return processor;
  }

  async match(request, server) {
    for (let processor of this._processors) {
      if (!(await processor.process(request, server))) return false;
    }

    return true;
  }

  equal(conditions) {
    if (!(conditions instanceof Object)) throw new Error('Request conditions set must be an object');

    for (let key in conditions) {
      if (!this._requestKeys.includes(key)) {
        throw new FMFException(`Invalid key "${key}" for request conditions configuration`);
      }

      // Run setters
      this[key].equal(conditions[key]);
    }

    return this;
  }
}

export default RequestMatcher;

import FMFException from '@/helpers/FMFException';

/**
 * The response configurator class is common to fixtures and presets and offers a set
 * of tools to configure response content and behavior.
 *
 * This class should be seen as an abstract class as it uses `_getCurrentResponseSet` of its
 * child to locate the response to configure
 *
 * ** Note : ** The response configurator does not contain logic to follow
 * calls count.
 *
 * @version 1.0.0
 * @since 2.0.0
 */
export class ResponseConfigurator {
  /**
   * Stores the server instance
   * @type {Server|null}
   * @since 2.0.0
   */
  server = null;

  /**
   * Default response for fixture or preset. For fixture, it will be used
   * when ordered responses are not matching defined call counts.
   * @type {Object}
   * @since 2.0.0
   */
  _any = {};

  /**
   * Allowed response keys
   * @type {Array}
   * @since 2.0.0
   */
  _responseKeys = ['body', 'delay', 'headers', 'status', 'statusText', 'wrapper', 'pattern', 'before', 'after'];

  /**
   * Response configurator constructor
   * @version 1.0.0
   * @since   2.0.0
   * @param   {Server}  [server=null] Server instance
   */
  constructor(server = null) {
    this.server = server;
  }

  /**
   * Sugar for chaining
   * @version 1.0.0
   * @since   2.0.0
   * @return  {ResponseConfigurator}  this
   */
  get with() {
    return this;
  }

  /**
   * Sugar for chaining
   * @version 1.0.0
   * @since   2.0.0
   * @return  {ResponseConfigurator}  this
   */
  get and() {
    return this;
  }

  /**
   * Set the response parameters based on the object provided
   * @version 1.0.0
   * @since   2.0.0
   * @param   {Object}  [params={}] Parameters
   * @see {@link ResponseConfigurator#_responseKeys} for available keys
   * @throw {FMFException} If a key is not valid
   */
  set(params = {}) {
    if (!(params instanceof Object)) throw new Error('Response set must be an object');

    for (let key in params) {
      if (!this._responseKeys.includes(key)) {
        throw new FMFException(`Invalid key "${key}" for response set configuration`);
      }

      // Run setters
      this[key](params[key]);
    }

    return this;
  }

  /**
   * Set the response body
   * @version 1.0.0
   * @since   2.0.0
   * @param   {String|Function}  body [description]
   * @return  {ResponseConfigurator}  this
   */
  body(body) {
    let response = this._getCurrentResponseSet();

    if (body === false) delete response.body;
    else response.body = body;

    return this;
  }

  /**
   * Set the time the server will wait before sending back response
   * @version 1.0.0
   * @since   2.0.0
   * @param   {Number}  delay Delay in ms
   * @return  {ResponseConfigurator}  this
   * @see {@link Fixture#sleep}
   */
  delay(delay) {
    let response = this._getCurrentResponseSet();

    if (delay === false) delete response.delay;
    else response.delay = parseInt(delay, 10);

    return this;
  }

  headers(headers) {
    if (headers && !(headers instanceof Object || headers instanceof Headers)) {
      throw new Error('Headers must be an object or an Headers instance');
    }

    if (headers instanceof Object) headers = new Headers(headers);

    let response = this._getCurrentResponseSet();

    if (headers === false) delete response.headers;
    else response.headers = headers;

    return this;
  }

  status(status) {
    let response = this._getCurrentResponseSet();

    if (status === false) delete response.status;
    else response.status = parseInt(status, 10);

    return this;
  }

  statusText(text) {
    let response = this._getCurrentResponseSet();

    if (text === false) delete response.statusText;
    else response.statusText = text;

    return this;
  }

  wrapper(wrapper) {
    let response = this._getCurrentResponseSet();

    if (wrapper === false) delete response.wrapper;
    else response.wrapper = wrapper;

    return this;
  }

  pattern(pattern) {
    let response = this._getCurrentResponseSet();

    if (pattern === false) delete response.pattern;
    else response.pattern = pattern;

    return this;
  }

  before(cb) {
    if (cb && !(cb instanceof Function)) {
      throw new Error('Before hook must be a function');
    }

    let response = this._getCurrentResponseSet();

    if (cb === false) delete response.before;
    else response.before = cb;

    return this;
  }

  after(cb) {
    if (cb && !(cb instanceof Function)) {
      throw new Error('Before hook must be a function');
    }

    let response = this._getCurrentResponseSet();

    if (cb === false) delete response.after;
    else response.after = cb;

    return this;
  }
}

export default ResponseConfigurator

export default class ServerHistory {
  _history = [];
  _call = null;
  _target = null;

  push(request, response) {
    this._history.push({
      request,
      response
    })
  }

  get request() {
    if (this._call) {
      const entry = this._history[this._call - 1].request;

      this._call = null;
      return entry;
    }

    this._target = 'request';
    return this;
  }

  get response() {
    if (this._call) {
      const entry = this._history[this._call - 1].response;

      this._call = null;
      return entry;
    }

    this._target = 'response';
    return this;
  }

  get first() {
    return this.atCall(1);
  }

  get second() {
    return this.atCall(2);
  }

  get third() {
    return this.atCall(3);
  }

  get last() {
    return this.atCall(this._history.length);
  }

  atCall(n) {
    if (this._target) {
      const entry = this._history[n-1][this._target];

      this._target = null;
      return entry;
    }

    this._call = n;
    return this;
  }

  all() {
    if (this._target) {
      const entries = this._history.map(entry => entry[this._target]);
      this._target = null;
      return entries;
    }
    return this._history;
  }

  reset() {
    this._history = [];
    this._call = null;
    this._target = null;
  }
}

import Server from './Server';
import Preset from './Preset';
import Fixture from './Fixture';
import FMFRequest from './helpers/FMFRequest';
import presets from '@/presets';

export default Server;

export {
  Fixture,
  FMFRequest,
  Preset,
  Server,
  presets
}

import ResponseConfigurator from './helpers/ResponseConfigurator';

export class Preset extends ResponseConfigurator {
  constructor(server, name, preset) {
    super(server);

    if (!name) throw new Error('You must provide a name to the preset');
    this.name = name;

    if (preset) {
      if (!(preset instanceof Object)) throw new Error('Preset options must be provided as an object');
      this.set(preset);
    }
  }

  _getCurrentResponseSet() {
    return this._any;
  }
}

export default Preset;

export default {
  default: {
    body: 'Hello world !',
    delay: 0,
    headers: {'content-type': 'text/html'},
    status: 200,
    statusText: 'OK'
  },

  json: {
    headers: {'content-type': 'application/json'},
    wrapper: body => JSON.stringify(body)
  },

  204: {
    body: null,
    headers: {'content-type': 'text/html'},
    status: 204,
    statusText: 'No Content'
  },

  400: {
    body: null,
    headers: {'content-type': 'text/html'},
    status: 400,
    statusText: 'Bad Request'
  },

  401: {
    body: null,
    headers: {'content-type': 'text/html'},
    status: 401,
    statusText: 'Unauthorized'
  },

  403: {
    body: null,
    headers: {'content-type': 'text/html'},
    status: 403,
    statusText: 'Forbidden'
  },

  404: {
    body: null,
    headers: {'content-type': 'text/html'},
    status: 404,
    statusText: 'Not found'
  },

  500: {
    body: null,
    headers: {'content-type': 'text/html'},
    status: 500,
    statusText: 'Internal Server Error'
  }
}

import _isEqual from 'lodash.isequal';

export default class BaseProcessor {
  _key = null;
  _not = false;
  _evaluate = null;

  constructor(key, matcher) {
    this._key = key;
    this._matcher = matcher;
  }

  get is() {
    return this;
  }

  get not() {
    this._not = !this._not;

    return this;
  }

  async _equal(current, expected, request) {
    if (expected instanceof Function) return await expected(current, this._key, request);
    if (expected instanceof RegExp) return expected.test(current);

    // Try to parse current as JSON to convert boolean and numbers
    try {
      current = JSON.parse(current);

      return _isEqual(current, expected);
    } catch (err) {
      return _isEqual(current, expected);
    }
  }

  equal(expected) {
    this._evaluate = async request => {
      let current = request[this._key];

      return await this._equal(current, expected, request);
    }

    return this._matcher;
  }

  async process(request, server) {
    let passed = await this._evaluate(request, server);

    return this._not ? !passed: passed;
  }
}

import BaseProcessor from '@/processors/BaseProcessor';
import FMFException from '@/helpers/FMFException';

export default class BodyProcessor extends BaseProcessor {
  _type = null;

  constructor(key, matcher, type) {
    super(key, matcher);
    this._type = type;
  }

  async _processBody(request) {
    let current;

    // Try to decode body
    switch (this._type.toLowerCase()) {
      case 'text':
        try {
          current = await request.clone().text();
        } catch (err) {
          /* istanbul ignore next */
          throw new FMFException('Unable to parse body as blob', err);
        }
        break;
      case 'json':
        try {
          current = await request.clone().json();
        } catch (err) {
          /* istanbul ignore next */
          throw new FMFException('Unable to parse body as JSON', err);
        }
        break;
      case 'formdata':
        try {
          current = await request.clone().formData();
        } catch (err) {
          /* istanbul ignore next */
          throw new FMFException('Unable to parse body as FormData', err);
        }
        break;
      case 'arraybuffer':
        try {
          current = await request.clone().arrayBuffer();
        } catch (err) {
          /* istanbul ignore next */
          throw new FMFException('Unable to parse body as Blob', err);
        }
        break;
      case 'blob':
        try {
          current = await request.clone().blob();
        } catch (err) {
          /* istanbul ignore next */
          throw new FMFException('Unable to parse body as Blob', err);
        }
        break;
      /* istanbul ignore next */
      default:
        throw new FMFException('Unknown body decoder callback')
    }

    return current;
  }

  equal(expected) {
    this._evaluate = async request => {
      const current = await this._processBody(request);

      return await this._equal(current, expected, request);
    }

    return this._matcher;
  }
}

import BaseProcessor from './BaseProcessor';

export default class BooleanProcessor extends BaseProcessor {

}

import BaseProcessor from './BaseProcessor';
import FMFException from '@/helpers/FMFException';

export default class HeadersProcessor extends BaseProcessor {
  _name = null;

  constructor(key, matcher, name) {
    super(key, matcher);
    this._name = name;
  }

  equal(expected) {
    this._evaluate = async request => {
      const headers = request.headers;

      /* istanbul ignore if */
      if (!(headers instanceof Headers)) throw new FMFException('Unable to extract headers from request');

      return await this._equal(this._name ? headers.get(this._name) : headers, expected);
    }

    return this._matcher;
  }
}

import BaseProcessor from './BaseProcessor';

export default class QueryProcessor extends BaseProcessor {

}

import BaseProcessor from './BaseProcessor';

export default class StringProcessor extends BaseProcessor {

}

import {FMFRequest, Fixture, Preset} from '.';
import ServerHistory from './helpers/ServerHistory';
import FMFException from './helpers/FMFException';
import presets from './presets';
import sinon from 'sinon';

/**
 * Build a mock server to respond to any fetch calls. It replaces
 * `window.fetch` with a [Sinon stub](https://sinonjs.org/releases/latest/stubs/). Therefore,
 * all functionnalities provided by stub are available
 *
 * **Note :** All the server data is stored in the current instance. That may have
 * unattended side effects when using the same instance through many test without
 * resetting it each time
 *
 * @since 1.0.0
 * @version 1.0.0
 * @author Liqueur de Toile <contact@liqueurdetoile.com>
 */
export class Server {
  /**
   * Store the fixtures loaded into the server or created on-the-fly
   * @type {Array}
   * @since 2.0.0
   * @see {@link Fixture}
   */
  _fixtures = [];

  /**
   * Store wether FMF shoud throw or send a 500 HTTP response when an error is raised
   * @type {Boolean}
   * @since 2.0.0
   * @see {@link Server#throwOnError}
   * @see {@link Server#warnOnError}
   */
  _throwOnError = false;

  /**
   * Store wether FMF shoud display a warning message in console when an error is raised
   * @type {Boolean}
   * @since 2.0.0
   * @see {@link Server#throwOnError}
   * @see {@link Server#warnOnError}
   */
  _warnOnError = true

  /**
   * Store the loaded presets and those created on-the-fly
   * @type {Object}
   * @since 2.0.0
   */
  _presets = {};

  /**
   * Store the server history
   * @type {ServerHistory}
   * @since 2.0.0
   */
  history = new ServerHistory();

  /**
   * Import the default presets into server
   * @version 2.0.0
   * @since   1.0.0
   * @author Liqueur de Toile <contact@liqueurdetoile.com>
   */
  constructor() {
    // Load presets
    for (let name in presets) {
      this._presets[name] = new Preset(this, name, presets[name]);
    }
  }

  /**
   * Start the server by stubbing `window.fetch`
   * @version 2.0.0
   * @since   1.0.0
   * @return  {Server}               Server instance
   */
  start() {
    /* istanbul ignore else */
    if (!this.running) {
      sinon.stub(window, 'fetch');
      this.stub.callsFake(this._processRequest.bind(this));
    }

    return this;
  }

  /**
   * Stop the server and, optionnally reset it
   * @version 2.0.0
   * @since   1.0.0
   * @param   {Boolean} [resetServer=false] If `true`, `stop` will also reset server (see {@link Server#reset})
   * @return  {Server}               Server instance
   */
  stop(resetServer = false) {
    if (this.running) window.fetch.restore();

    if (resetServer) this.reset();

    return this;
  }

  /**
   * Reset the server configuration to default, clear server history and stub history
   * @version 2.0.0
   * @since   1.0.0
   * @param   {Boolean} [resetStub=true] If `true`, the stub history will also be resetted
   * @return  {Server}               Server instance
   */
  reset(resetStub = true) {
    if (this.running && resetStub) this.stub.resetHistory();
    this.history.reset();
    this._fixtures = [];

    return this;
  }

  /**
   * Tells the server to display a warning in console when an error is raised or when
   * something seems to went wrong in configuration.
   *
   * Default settings is true
   *
   * @version 1.0.0
   * @since   2.0.0
   * @param   {Boolean}  warnOnError `true` will display warnings
   * @return  {Server}               Server instance
   */
  warnOnError(warnOnError) {
    this._warnOnError = !!warnOnError;

    return this;
  }

  /**
   * Set the behavior of the server when an Error is thrown. If set to `true`, the server will
   * also throw the error at runtime. If set to false, it will respond with a 500 HTTP error
   *
   * At default, the server is set to throw on error that will usually be
   * the most suitable behavior when running tests to discard FMF failures.
   *
   * **note** Only errors thrown during requests processing are affected by this parameter.
   * Errors that occured on settings processing will always be raised
   *
   * @version 1.0.0
   * @since   2.0.0
   * @param   {Boolean}  throwOnError If `true` server will throw
   * @return  {Server}               Server instance
   * @see {@link Server#_onError}
   */
  throwOnError(throwOnError) {
    this._throwOnError = !!throwOnError;

    return this;
  }

  /**
   * Displays a warning message in console. It can be overridden
   * to swap to another notification system
   * @version 1.0.0
   * @since   2.0.0
   * @param   {String|Error}  error Error description
   */
  warn(error) {
    console.warn(error.toString()); // eslint-disable-line
  }

  /**
   * Check if server is currently running by trying to access a stub property
   * @version 1.0.0
   * @since   1.1.0
   * @return  {Boolean}
   */
  get running() {
    return window.fetch.reset instanceof Function;
  }

  /**
   * Exposes the underlying stub or throws error if server is not started
   * @version 1.0.0
   * @since   1.1.0
   * @return  {Object}  Sinon stub
   */
  get stub() {
    if (this.running) return window.fetch;

    throw new FMFException('Server is not started');
  }

  /**
   * Returns the selected preset or a new one based on name resolution.
   *
   * It allow a quick preset creation or edition that can be configured at once
   * through the object provided within this call or with the classic
   * ResponseConfigurator
   *
   * @version 1.0.0
   * @since   2.0.0
   * @param   {String}  name        Preset name
   * @param   {Object}  [preset={}] Preset content
   * @return {Preset}
   * @see {@link ResponseConfigurator}
   */
  preset(name, preset = {}) {
    if (this._presets[name]) return this._presets[name].set(preset);

    let newPreset = new Preset(this, name, preset);

    this._presets[name] = newPreset;

    return newPreset;
  }

  /**
   * Import a fixture into the server pool. Fixture can be provided as a
   * fixture instance or as a configuration object
   * @version 1.0.0
   * @since   2.0.0
   * @param   {Fixture|Object|Array}  fixtures Fixture(s) to import
   * @return  {Server}               Server instance
   * @throws {FMFException} If fixture cannot be parsed
   */
  import(fixtures) {
    if (!(fixtures instanceof Array)) fixtures = [fixtures];

    for (let fixture of fixtures) {
      if (fixture instanceof Fixture) {
        fixture.server = this;
        this._fixtures.push(fixture);
      }
      else if (fixture instanceof Object) {
        let f = new Fixture(this);
        let conditions = fixture.on || fixture.when;

        if (!fixture.respond) throw new FMFException('Fixture provided as object must have a respond property');
        /* istanbul ignore else */
        if (conditions) f.on.equal(conditions);
        f.respond.set(fixture.respond);

        this._fixtures.push(f)
      }
      else throw new FMFException('Invalid fixture provided');
    }

    return this;
  }

  /**
   * This getter is used when configuring a fixture in-the-fly. It will return
   * and register a new Fixture and set it to `matching` mode
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Fixture}  New Fixture
   */
  get on() {
    const fixture = new Fixture(this)

    this._fixtures.push(fixture);

    fixture._mode = 'on';

    return fixture.on;
  }

  /**
   * Alias for {@link Server#on}
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Fixture}  New fixture
   */
  get when() {
    return this.on;
  }

  /**
   * Returns the existing registered on the server or create and register a new fallback fixture
   * to configure
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Fixture}  Fallback fixture
   * @see {@link Server#_getDefaultFixture}
   */
  get fallback() {
    return this._getDefaultFixture();
  }

  /**
   * Returns the existing registered on the server or create a new fallback fixture
   * to configure
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Fixture}  Fallback fixture
   */
  _getDefaultFixture() {
    // If a default fixture exists, return it
    const index = this._fixtures.findIndex(f => f._matcher === null);

    if (index >= 0) return this._fixtures[index];

    // Create a new default Fixture and register it
    const fixture = new Fixture(this);

    this._fixtures.push(fixture);
    return fixture;
  }

  /**
   * Process the respond call when called from a fixture to allow chainable
   * fixtures on-the-fly configuration
   * @version 1.0.0
   * @since   2.0.0
   * @param   {Object}  [fixture={}] Calling fixture or void object if not called from a fixture
   * @return  {Fixture}              Return either the default fixture or set the current to `respond` mode
   */
  _processRespond(fixture = {}) {
    if (fixture._mode === 'respond') fixture = this._getDefaultFixture();

    fixture._mode = 'respond';

    return fixture;
  }

  /**
   * Getter used when configuring fixture on-the-fly
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Fixture}  Return either the default fixture or set the current to `respond` mode
   * @see {@link Server#_processRespond}
   */
  get respond() {
    return this._getDefaultFixture();
  }

  /**
   * Seeks for matching fixtures when processing a request
   *
   * An error will be raised if no fixtures have been set or if no matching fixtures have been
   * found.
   *
   * FMF will also send a warning to the console
   *
   * @version 1.0.0
   * @since   2.0.0
   * @param   {FMFRequest}  request Request
   * @return  {Promise}         Resolved in fixture instance
   * @throws  {FMFException}   If no fixtures are defined or no matching fixtures found
   */
  async _findFixture(request) {
    let matches = [];
    let fallback = null;

    if (!this._fixtures.length) throw new FMFException('No fixtures defined');

    for (let fixture of this._fixtures) {
      // Do not register fallback fixture
      if (fixture._matcher === null) {
        fallback = fixture;
        continue;
      }
      if (await fixture.match(request)) matches.push(fixture);
    }

    if (!matches.length) {
      if (!fallback) throw new FMFException('Unable to find a matching fixture for the current request and no fixture is set as fallback');
      matches[0] = fallback;
    }

    if (matches.length > 1) {
      this.warn(`FMF : Server found ${matches.length} fixtures matching the request "${request.url}". Using the first one.`); // eslint-disable-line
    }

    return matches[0];
  }

  /**
   * Process the incoming request and update history
   * @version 1.0.0
   * @since   2.0.0
   * @param   {String|Request}  request Incoming request
   * @param   {Object}  [init]  request options
   * @return  {Promise}         Response
   * @throws  {FMFException}  If request processing have failed
   */
  async _processRequest(request, init) {
    try {
      // Build FMFRequest object
      request = new FMFRequest(request, init);

      // Locate matching fixture
      let fixture = await this._findFixture(request.clone());

      // Prepare response
      let response = await fixture.getResponse(request.clone());

      // Store request in history
      this.history.push(request.clone(), response.clone());

      return response;
    } catch (err) {
      if (this._warnOnError) this.warn(err);
      if (this._throwOnError) /* istanbul ignore next */ throw (err instanceof FMFException ? err : new FMFException('Request process failure', err));

      return new Response(err.stack, {
        'content-type': 'text/html',
        status: 500,
        statusText: err.toString()
      })
    }
  }

  /**
   * Returs the number of calls made to server since start or last reset
   * @version 1.0.0
   * @since   2.0.0
   * @return  {Number}  Number of requests received
   */
  get calls() {
    return this.stub.callCount;
  }

  /**
   * Returns the last request received by the server
   * @version 1.0.0
   * @since   2.0.0
   * @return  {FMFRequest}
   * @see {@link ServerHistory}
   */
  get request() {
    return this.history.last.request;
  }

  /**
   * Returns the last response received by the server
   * @version 1.0.0
   * @since   2.0.0
   * @return  {FMFRequest}
   * @see {@link ServerHistory}
   */

  get response() {
    return this.history.last.response;
  }
}

export default Server;

# Getting started

** For installation tips, please consult [documentation home](index.html#installation). **

** Note on ES6 syntax : ** In all examples here, we're using ES6 modules import and, sometimes, the new spread operator. We're also using async/await syntax for asynchronous calls. All are supported natively by modern browsers.

FMF itself have been transpiled with Babel to support only alive browsers that share more than 5% of the market. It means that FMF is really not designed to test apps which are willing to support really old browsers as it will certainly be unsupported by them when testing. Nevertheless, you can still try to tweak the build to increase browsers coverage but with no guarantee.

At its most basic intend, FMF can be simply used as a convenient way to trap a remote `fetch` call and send a given response. For this to work, the server must have been started and provided with the response. At the end, the server must be stopped in order to resume on native `fetch` API.

Here's a simple example :

```javascript
import Server from 'fetch-mock-fixtures';

const server = new Server();

server.start();

server.respond.with.body('Hello world !');

let response = await fetch(/** can have any arguments */);
let data = await response.text();

console.log(data); // will output Hello world !

// What about the next call ?
response = await fetch(/** can have any arguments */);
data = await response.text();

console.log(data); // will output Hello world again !

server.stop();
```

Nothing too fancy here : we're starting the server, setting it to respond with 'Hello world !', running fetch twice and stop the server.

Behind the scene, we have :
- overridden `window.fetch` to intercept calls
- Set up a single fixture to the server that matches all incoming requests
- restored `window.fetch` to its native state

Please read other parts of the documentation for a more advanced usage.

# Response configuration
Fixtures and presets share the same response configurator. It allows to set up response content and/or adapt request processing behavior.

## Configure response
From the fixture or the preset, you can either use the `set` method and/or use the BDD style :
```javascript
const server = new Server()

server.respond // will register a new Fixture
  .set({
    status: 200
  })
  .headers({'content-type': 'application/json'})
  .body(body => JSON.stringify({message: 'Hellow world !'}));

// Server will now respond to all requests with a JSON response and status 200
```
The response properties name are the same between object set approach and BDD style.
The last assignment (no matter the way you're doing it) will override the previous. You can remove an option by providing false as value :

```javascript
const server = new Server()

server.respond // will register a single fixture
  .body(body => JSON.stringify({message: 'Hello world !'}));

// Remove body callback
server.respond // will fetch the fixture
  .body(false);
```

### Default response values
There's no default value for a fixture/preset response. You can use set up a preset and use it to automatically populate one
or more response options of a fixture.

### Response configuration persistence
Fixtures are stored within server and persists until server is reset. If you share the same server between many tests, any changes to fixtures
configuration will persist into next tests. This can sometimes be tricky if you're updating an option of the fallback fixture in one test. See [fixtures](fixtures.html) for more informations. This can also be really useful as you can create fixtures only once and share them along all tests.

## Available response options
Each response option can be removed by affecting the false value to it. ** It is evaluated as a strict comparison.**

FMF doesn't provide any default values, but Response implementation usually set up a status 200 with 'text/html' encoding to a new Response object when no options is provided.

Option  | Allowed value(s)  |  Description
--|---|--
body  |  null &verbar; Blob &verbar; BufferSource &verbar; FormData &verbar; ReadableStream &verbar; URLSearchParams &verbar; USVString &verbar; Function  | You can use any of the available types for a native [Response](https://developer.mozilla.org/fr/docs/Web/API/Response/Response) object. FMF also accepts a callback that will return the body content or alter the response (see [fixture lifecycle](fixtures.html#fixture-lifecycle))
delay  | Number  | The fixture response will be delayed by X ms
headers  | Object &verbar; [Headers](https://developer.mozilla.org/en-US/docs/Web/API/Headers)  |  The object will be used to instantiate the Headers
status  | Number |  Status code of the response (2XX - 5XX). Some status code may have some requirements. For instance, trying to set up a body with a 204 status code will fail. It is not a FMF behavior but from native Response object.
statusText  |  String | Status text along the status code
wrapper  | Function | Wrapper callback that transforms body. See [wrappers](#using_wrappers)
preset  |  String | **Only available within a fixture**. See [presets](presets.html)
pattern  | String | Pattern to apply to extract parameters from incoming request url. See [patterns](#using_pattern)
before  | Function  |  Callback called before the response is built. See [fixture lifecycle](fixtures.html#fixture-lifecycle)
after  |  Function |  Callback called after the response have been built.  See [fixture lifecycle](fixtures.html#fixture-lifecycle)

## Using wrappers
Wrappers are used as body processors when preparing the response. Body stored from fixture is provided and the processed body must be returned. The main goal is to get rid of little transformations when providing the body to the fixture. Only one wrapper is allowed per response.

For instance, let's say we're working on a JSON API that expects the server's response to be always wrapped in the same patterns. You can use wrapper and two global presets to get rid of emulating this behavior each time you're creating a fixture :

```javascript
import {presets, Server} from 'fetch-mock-fixtures';

// Add the presets to global presets object
// You can do it in your tests bootstrap
presets = Object.assign(presets, {
  'api-success': {
    headers: {'content-type': 'application/json'},
    wrapper: body => JSON.stringify({
      success: true,
      data: body
    })
  },
  'api-failure': {
    headers: {'content-type': 'application/json'},
    wrapper: body => JSON.stringify({
      success: false
      error: body
    })
  }
})

// In tests scripts
const server = new Server()

server.respond.with.preset('api-success').and.body({
  id: 1,
  name: 'foo'
});

// Parsed JSON response will be {success: true, data: {id: 1, name: 'foo'}}

```
## Using patterns
Patterns are a way to automatically extract parameters from the url. They will be provided as an object and first argument to the body callback (see fixtures#body_callback).

To extract params, url is parsed with [`path-to-regexp`](https://github.com/pillarjs/path-to-regexp#readme). Please refer to this for advanced syntax.

Here's a simple example that use extract user id from url :

```javascript
import {Server} from 'fetch-mock-fixtures';

users = [
  {id: 1, name: 'foo'},
  {id: 2, name: 'bar'},
  {id: 3, name: 'baz'},
]

server.start().respond
  .with.pattern('/api/users/:id?')
  .and.body({id} => return id ? users.find(user => user.id === id) || users)

```

# Presets

## Using presets
You can tell a fixture to use a preset to build its response by simply calling its `preset` method with the name of the preset :

```javascript
const server = new Server();

server.respond.with.preset(400);
```

The preset is only evaluated and merged by the fixture at request time. You can easily override preset through [fixture response configuration](fixtures.html).

If the preset is not found in the server instance, an exception will be raised at request time.

## adding/editing a preset
It can be done by providing an object with its `set` method and/or using BDD style syntax. Alternatively, the configuration object can be provided to `server.preset` :

```javascript
const server = new Server();

// In the server.preset call
server.preset('myPreset', {
  status: 250
  statusText: 'weird status'
});

// Another syntax, the same result
server.preset('myPreset').set({status: 250}).statusText('weird')
```
You can have a look at the [response configuration](response-configuration.html) for more details about the available options.

## Preset scope
A preset can be added to a single server instance or in global scope.

To add a preset to server instance, simply create it with `server.preset`.

To add a preset to the global scope, just amend the `presets` property of the global export :

```javascript
import {presets} from 'fetch-mock-fixtures';

presets.myPreset = {
  status: 250
  statusText: 'weird status'
} // myPreset will now be available globally
```

## Built-in presets
FMF already have commonly used built-in presets. See [`presets.js`](../file/src/presets.js.html) for details.

import Server from '@';

const server = new Server();

// Callback to dynamically fetch fixture file based on path
const fetchFixture = function(server, request, response) {
  const path = this.getPath(request, response)
  let newResponse;

  try {
    newResponse = require(`fixtures/${path}.fixture.js`).default;
  } catch (err) {
    throw server.preset(404);
  }

  newResponse = Object.assign(
    {},
    response,
    newResponse,
    {
      headers: {'content-type': 'application/json'},
      wrapper: body => JSON.stringify(body)
    }
  );

  return newResponse;
}

describe('Webpack require dynamic fixture example', function() {
  before(() => {
    server.start().respond.before(fetchFixture);
  })

  after(() => {
    server.stop();
  })

  it('should load a fixture without pattern', async function() {
    let response, data;

    response = await fetch('/api/users');
    response.status.should.equal(200);
    data = await response.json();
    data.should.deep.equal([
      {
        id: 1,
        name: 'foo'
      },
      {
        id: 2,
        name: 'bar'
      }
    ]);

    response = await fetch('/api/users/1');
    response.status.should.equal(200);
    data = await response.json();
    data.should.deep.equal({
      id: 1,
      name: 'foo'
    });

    response = await fetch('/api/users', {method: 'POST'});
    response.status.should.equal(201);
    data = await response.json();
    data.should.deep.equal({
      id: 3,
      name: 'baz'
    });

    response = await fetch('/api/users/5');
    response.status.should.equal(404);
  })

  it('should load a fixture with pattern', async function() {
    server.respond.pattern('/api2/users/:id?');

    let response, data;

    response = await fetch('/api2/users');
    response.status.should.equal(200);
    data = await response.json();
    data.should.deep.equal([
      {
        id: 1,
        name: 'foo'
      },
      {
        id: 2,
        name: 'bar'
      }
    ]);

    response = await fetch('/api2/users/1');
    response.status.should.equal(200);
    data = await response.json();
    data.should.deep.equal({
      id: 1,
      name: 'foo'
    });

    response = await fetch('/api2/users/5');
    response.status.should.equal(404);
  })
})

import Server from '@';
import PouchDB from 'pouchdb';

let db;

/** Data that will be stored and restored each time in database */
const fixtures = [
  {
    _id: 'users/1',
    id: 1,
    name: 'foo'
  },
  {
    _id: 'users/2',
    id: 1,
    name: 'bar'
  },
  {
    _id: 'objects/1',
    id: '1',
    value: 'object1'
  }
];

/** Callback to search/upsert database */
const fetchFixture = async function(params, {request, response, server}) {
  if (!params.model) throw server.preset(404);

  const get = async function({model, id}) {
    if (id) {
      try {
        const _id = `${model}/${id}`;
        return await db.get(_id);
      } catch (err) {
        throw server.preset(err.status);
      }
    }

    let docs = await db.allDocs({
      include_docs: true
    });

    return docs.rows.filter(row => row.id.indexOf(model) === 0).map(row => row.doc)
  }

  const upsert = async function({model, id}) {
    if (!id && request.method !== 'POST') {
      throw server.preset(400);
    }

    if (id && request.method === 'POST') {
      throw server.preset(400);
    }

    id = id || (await get({model})).length + 1;

    const _id = `${model}/${id}`;
    let doc;
    let data = await request.json();

    try {
      doc = await db.get(_id);
    } catch (err) {
      if (err.status === 404) {
        doc = Object.assign({_id}, data);
      } else throw server.preset(err.status);
    }

    try {
      await db.put(doc);
    } catch (err) {
      throw server.preset(err.status);
    }

    return doc;
  }

  if (request.method === 'POST') response.status = 201;
  if (request.method === 'GET') return get(params);
  if (['POST', 'PUT', 'PATCH'].includes(request.method)) return upsert(params);
}

const server = new Server();

describe('Dynamic fixture from database', function() {
  beforeEach(async () => {
    db = new PouchDB('FMF');

    await db.bulkDocs(fixtures);
    server.start()
      .respond
      .with.preset('json')
      .with.body(fetchFixture)
      .with.pattern('/apidb/:model/:id?');
  })

  afterEach(async () => {
    await db.destroy();
    server.stop();
  })

  it('should fetch all rows from a model', async function() {
    let response = await fetch('/apidb/users');
    let data = await response.json();

    data.length.should.equal(2);
  })

  it('should fetch once record from a model', async function() {
    let response = await fetch('/apidb/users/1');
    let data = await response.json();

    data.name.should.equal('foo');
  })

  it('should create a new record', async function() {
    let response = await fetch('/apidb/users', {
      method: 'POST',
      body: JSON.stringify({
        name: 'baz'
      })
    });

    response.status.should.equal(201);
    let data = await response.json();

    data.name.should.equal('baz');
  })
})

import {Server} from '@';

const server = new Server();

describe('Errors management', function() {
  before(() => server.start())
  beforeEach(() => server.reset())
  after(() => server.stop())

